//TODO move the plugin/model to a feathers-mongoose style definition
//this will involve dealing with passportLocalMongoose....
var mongoose = require('mongoose');
var _ = require('lodash');
var ObjectId = mongoose.Schema.Types.ObjectId;
passportLocalMongoose = require('passport-local-mongoose');


// Set up the schema
var schema = new mongoose.Schema({
  /*
  * Passport-Local Mongoose will add a username, hash and salt field
  */
  type: {
    type: String,
    default: 'user',
    enum: ['user', 'admin'],
  },
  updated_at: {
    type: Date,
    default: new Date(),
  },
  created_at: {
    type: Date,
    default: new Date(),
  }
});

schema.pre('save', function(next) {
  var that = this
  that.updated_at = _.isUndefined(that.created_at) ? that._id.getTimestamp() : new Date();
  if (!that.created_at)
    that.created_at = that._id.getTimestamp()
  next()
})

//Plugins
schema.plugin(passportLocalMongoose);

module.exports = mongoose.model('User', schema);
