var colors = require('colors/safe');

// set single property
var error = colors.red;
error('this is red');

// set theme
colors.setTheme({
  info: 'green',
  data: 'grey',
  bright: 'cyan',
  warn: ['orange'],
  dull: ['blue', 'dim'],
  error: ['red', 'bold', 'bgWhite'],
  line: ['white', 'bold', 'bgWhite']
});

module.exports = colors;
